mapper.select:
    select  t1.apply_name, t1.application_shop_time, t1.phone, t1.application_time, t2.apply_type,t1.imgs_approval_time,t1.address_approval_time  from store_info t1  
    LEFT JOIN  cobra_apply_store t2 ON t1.apply_store_id=t2.apply_store_id
	where t1.applicant=#{applicant}

        and status = #{status,jdbcType=INTEGER}


        and address_audit_status = #{addressAuditStatus,jdbcType=INTEGER}


        and imgs_audit_status = #{imgsAuditStatus,jdbcType=INTEGER}

    select  t1.apply_name, t1.application_shop_time,t2.paid_money,t1.application_time,t2.paymoney_type,t1.imgs_approval_time,t1.address_approval_time  from store_info t1  
    LEFT JOIN  cobra_apply_store t2 ON t1.apply_store_id=t2.apply_store_id
	where 1=1

        and status = #{status,jdbcType=INTEGER}


        and address_audit_status = #{addressAuditStatus,jdbcType=INTEGER}



        	and address_approver = #{addressApprover,jdbcType=INTEGER}



        	and imgs_approver = #{imgsApprover,jdbcType=INTEGER}



        and imgs_audit_status = #{imgsAuditStatus,jdbcType=INTEGER}
